import React from 'react';

const causes = [
  {
    category: 'Medical',
    title: 'Donate For Treatment And Medicine.',
    description: 'Funds Go Towards Surgeries, Treatments, And Doctor Visits. Provides Life-Saving Medications And Essential Drugs',
    progress: 60,
    raised: '$600',
    goal: '$1,000',
    image: 'https://placehold.co/300x200',
  },
  {
    category: 'Homeless',
    title: 'Children We Work With',
    description: 'Provides Secure And Comfortable Housing Many Children Lack A Safe Place To Live.',
    progress: 85,
    raised: '$8,500',
    goal: '$10,000',
    image: 'https://placehold.co/300x200',
  },
  {
    category: 'Education',
    title: 'Help For Education',
    description: 'Help Us Provide Educational Opportunities To Homeless Children. Many Children Without Homes Miss Out On The Chance To Go To School.',
    progress: 90,
    raised: '$9,000',
    goal: '$10,000',
    image: 'https://placehold.co/300x200',
  },
  {
    category: 'Food',
    title: 'Help For Food',
    description: 'Help Us Provide Nutritious Meals To Homeless Children. Many Children Go Hungry Every Day, Lacking Access To Food For Healthy Growth And Development.',
    progress: 75,
    raised: '$1,500',
    goal: '$2,000',
    image: 'https://placehold.co/300x200',
  },
];

const DonationCauseCards = () => {
  return (
    <div className="container">
      <div className="header">
        <h2 className="subtitle">Latest Causes</h2>
        <h1 className="title">Find The Popular Cause And Donate Them</h1>
      </div>
      <div className="causesContainer">
        {causes.map((cause, index) => (
          <div key={index} className="causeCard">
            <img src={cause.image} alt={`${cause.category} cause`} className="image" />
            <div className="cardContent">
              <h3 className="category">{cause.category}</h3>
              <h2 className="cardTitle">{cause.title}</h2>
              <p className="description">{cause.description}</p>
              <div className="progressContainer">
                <div className="progressText">
                  <span>Donatone</span>
                  <span>{cause.progress}%</span>
                </div>
                <div className="progressBar">
                  <div className="progress" style={{ width: `${cause.progress}%` }}></div>
                </div>
                <div className="raisedGoal">
                  <span>Raised: {cause.raised}</span>
                  <span>Goal: {cause.goal}</span>
                </div>
                <button className="donateButton">Donate Now</button>
              </div>
            </div>
          </div>
        ))}
      </div>
      <style>{`
     
        .header {
          margin-bottom: 16px;
        }
        .subtitle {
          color: #777;
          font-size: 18px;
        }
        .title {
          font-size: 36px;
          font-weight: bold;
        }
        .causesContainer {
          display: flex;
          gap: 16px;
          overflow-x: auto;
        }
        .causeCard {
          background-color: #fff;
          color: #333;
          border-radius: 8px;
          box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
          width: 320px;
          flex-shrink: 0;
        }
        .image {
          border-top-left-radius: 8px;
          border-top-right-radius: 8px;
          width: 100%;
          height: 200px;
          object-fit: cover;
        }
        .cardContent {
          padding: 16px;
        }
        .category {
          color: #555;
          font-size: 14px;
        }
        .cardTitle {
          font-size: 24px;
          font-weight: bold;
          margin-top: 8px;
        }
        .description {
          color: #777;
          margin-top: 8px;
        }
        .progressContainer {
          margin-top: 16px;
        }
        .progressText {
          display: flex;
          justify-content: space-between;
          font-size: 14px;
        }
        .progressBar {
          background-color: #eee;
          border-radius: 8px;
          height: 10px;
          margin-top: 8px;
          overflow: hidden;
          width: 100%;
        }
        .progress {
          background-color: #007bff;
          height: 100%;
        }
        .raisedGoal {
          display: flex;
          justify-content: space-between;
          font-size: 14px;
          margin-top: 8px;
        }
        .donateButton {
          background-color: #28a745;
          color: #fff;
          border: none;
          border-radius: 8px;
          padding: 12px;
          width: 100%;
          margin-top: 16px;
          cursor: pointer;
          transition: background-color 0.3s;
        }
        .donateButton:hover {
          background-color: #218838;
        }
      `}</style>
    </div>
  );
};

export default DonationCauseCards;
